    const params = useParams("")

    const [movies, setMovies] = useState([])


    useEffect((search) => {
        fetch(`https://api.themoviedb.org/3/search/movie/?api_key=41514cf9c5004dbe47144dbf1928e39c&query=${params.movie}`)
