import { useState, useEffect } from "react"
import { Link } from 'react-router-dom'
import Slider from "react-slick";
import Carousel from 'react-material-ui-carousel'
import AliceCarousel from 'react-alice-carousel';
import TopRatedStyle from ".//TopRatedStyle.css";
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';


const baseImgUrl = "https://image.tmdb.org/t/p"
const size = "w500"


const TopRated = () => {

    const [topRated, setTopRated] = useState([])

    useEffect(() => {
        fetch("https://api.themoviedb.org/3/movie/top_rated/?api_key=41514cf9c5004dbe47144dbf1928e39c")
            .then(res => res.json())
            .then(data => setTopRated(data.results))

    }, [])

    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 5,
    };
    console.log(topRated)
    return (

        <div>


            <h1>toprated</h1>


            <Box
                sx={{
                    display: 'flex',
                    flexWrap: 'nowrap',
                    '& > :not(style)': {
                        m: 1,
                        width: 600,
                        height: 1000,
                        slidesToScroll: 10,
                    },
                }}
            >

                {topRated.map(moviestoprated => (

                    // <Slider {...settings}>

                    < Link to={`/toprated/${moviestoprated.id}`}>


                        <Paper elevation={3} />
                        <Carousel>
                            <h2> {moviestoprated.title}</h2>
                            <img src={`${baseImgUrl}/${size}${moviestoprated.poster_path}`} />
                        </Carousel>
                        <Paper />


                    </Link>

                    // </Slider>
                ))}

            </Box>




        </div >

    )
}


export default TopRated
