import Slider from "react-slick";
import "./Carrauselstyle.css"
import { useState, useEffect } from "react"
import { FaChevronLeft, FaChevronRight } from 'react-icons'


const Carrousel = () => {
    const [movies, setMovies] = useState([])
    useEffect(() => {
        fetch("https://api.themoviedb.org/3/movie/popular/?api_key=41514cf9c5004dbe47144dbf1928e39c")
            .then(res => res.json())
            .then(data => setMovies(data.results))

    }, [])

    return (
        <div >
            <button>
                <FaChevronLeft />
            </button>
            <button>
                <FaChevronRight />
            </button>
            <Slider
                dots={true}
                arrows={true}
                infinite={false}
                speed={500}
                slidesToShow={4}
                slidesToScroll={4}
                initialSlide={0}


            >

                {movies.map((movie) => (



                    <img className="img" src={`https://image.tmdb.org/t/p/w300/${movie.poster_path}`} />

                ))}


            </Slider>
        </div>
    )
}

export default Carrousel
