import Slider from "react-slick";
import "./Carrauselstyle.css"
import { useState, useEffect } from "react"
import { CardMedia, ImageList, ImageListItem } from "@mui/material";
import { Button, Link } from "@mui/material"


const Carrousel = () => {
    const [movies, setMovies] = useState([])
    useEffect(() => {
        fetch("https://api.themoviedb.org/3/movie/now_playing/?api_key=41514cf9c5004dbe47144dbf1928e39c")
            .then(res => res.json())
            .then(data => setMovies(data.results))

    }, [])

    return (
        <div className="container" >

            <Slider
                dots={true}
                arrows={true}
                slidesToShow={1}
                // adaptiveHeight={true}
                initialSlide={0}
                autoplay={true}
                autoplaySpeed={2000}
            >

                {movies.map((movie) => (
                    <div className="arrousel" key={movie.id}>
                        <img key={movie.id}
                            className="img-carrusel"
                            src={`https://image.tmdb.org/t/p/original/${movie.poster_path}`} />
                        <Button size="small" color="error">

                            <Link
                                sx={{
                                    textDecoration: "none",
                                    color: "#fafafa"
                                }}
                                href={`/${movie.id}`}>Entrar</Link>
                        </Button>
                    </div>
                ))}


            </Slider>

        </div >
    )
}

export default Carrousel
