import { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";

import CardItem from "./CardItems";


const SearchMovies = () => {
    const [valorDelInput, setValorDelInput] = useState("")
    const [movies, setMovies] = useState([])
    const [searchParams, setSearchParams] = useSearchParams({
        query: "",
    })


    handleSubmit = (e) => {
        e.preventDefault();
        useEffect(() => {
            fetch(`https://api.themoviedb.org/3/search/movie?pi_key=41514cf9c5004dbe47144dbf1928e39c${searchParams.get("query")}`)
                .then(res => res.json())
                .then(data => {
                    setMovies(data.results)
                })
        }, [searchParams])

    }


    const handleChange = (e) => {
        setValorDelInput(e.target.value)
    }



    return (

        <div>
            <h1>Search</h1>
            <p>Nombre del personaje</p>
            <form>
                <input
                    type="text"
                    onChange={handleChange}
                    value={valorDelInput}
                ></input>
                <input onSubmit={handleSubmit}> </input>
                {movies.map(movie => <h1>{movie.original_title}</h1>)}
            </form>
        </div>
    )


}
export default SearchMovies
