    //             setMovies(data.results)
    //         })
    // }, [searchParams])





    return (
        <div>
            <div className="searchmovies">

                {movies.map((movie) => (

                    <div >
                        <h1> {movie.title} </h1>
                        <img className="img" src={`https://image.tmdb.org/t/p/original/${movie.poster_path}`} />
                    </div>
                ))}

            </div>
        </div>

    )


}
export default SearchMovies
