import { useState, useEffect, } from "react"
import { Link, } from 'react-router-dom'
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';


const baseImgUrl = "https://image.tmdb.org/t/p"
const size = "w200"
const responsive = {
    desktop: {
        breakpoint: { max: 3000, min: 1024 },
        items: 3,
        slidesToSlide: 3 // optional, default to 1.
    },
    tablet: {
        breakpoint: { max: 1024, min: 464 },
        items: 2,
        slidesToSlide: 2 // optional, default to 1.
    },
    mobile: {
        breakpoint: { max: 464, min: 0 },
        items: 1,
        slidesToSlide: 1 // optional, default to 1.
    }
};

const TopRated = () => {

    const [toprated, setTopRated] = useState([])

    useEffect(() => {
        fetch("https://api.themoviedb.org/3/movie/top_rated/?api_key=41514cf9c5004dbe47144dbf1928e39c")
            .then(res => res.json())
            .then(data => setTopRated(data.results))

    }, [])


    console.log(TopRated)
    return (
        <Carousel
            swipeable={false}
            draggable={false}
            showDots={true}
            responsive={responsive}
            // means to render carousel on server-side.
            autoPlaySpeed={1000}
            keyBoardControl={true}
            customTransition="all .5"
            transitionDuration={500}
            slidesToShow={5}
            slidesToScroll={5}
            dotListClass="custom-dot-list-style"
            itemClass="carousel-item-padding-40-px"
        >
            <div>
                <h1>TopRated</h1>
                <Card sx={{ minWidth: 500 }}>
                    {toprated.map(moviestoprated => (
                        < Link to={`/popular/${moviestoprated.id}`}>

                            <h2>{moviestoprated.title}</h2>
                            <img src={`${baseImgUrl}/${size}${moviestoprated.poster_path}`} />

                        </Link>
                    ))}
                </Card>
            </div>
        </Carousel>
    )
}

export default TopRated
